import React, { Component } from 'react';

import FirstImpression from './first-impression.component';
import About from './about.component';
import Portfolio from './portfolio.component';
import { thisExpression } from '@babel/types';

class Home extends Component {
  state = {
    counters: [
      {
        id: 1,
        title: 'Blog1',
        content:
          'Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      },
      {
        id: 2,
        title: 'Blog2',
        content:
          'Jaz sem en bimbo. Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      },
      {
        id: 3,
        title: 'Blog3',
        content:
          'Ma kej je tma. Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      },
      {
        id: 4,
        title: 'Blog4',
        content:
          'Lorem in ipsum bimbum. Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      },
      {
        id: 5,
        title: 'Blog5',
        content:
          'Wtf omg jbtg. Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      },
      {
        id: 6,
        title: 'Blog6',
        content:
          'Lorem ipsum dolor sit amet consectetur adipisicing elit. Deserunt, veniam a magnam, animi neque sunt quam nam minus voluptatum velit esse voluptas quos nostrum harum deleniti atque praesentium eligendi quas?'
      }
    ],
    maxCounters: 1,
    projects: []
  };

  componentDidMount() {
    fetch('/api/project')
      .then(res => {
        console.log(res);
        if (res.status === 200) {
          console.log('---------------------PROJECTS-------');
          const projects = res.json();
          this.setState({
            projects: projects,
            maxCounters: projects.length
          });
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        console.error(err);
      });
  }

  render() {
    return (
      <div
        id="spy-wrapper"
        data-spy="scroll"
        data-target="#mainNav"
        data-offset="0"
      >
        <FirstImpression />
        <Portfolio />

        {this.state.counters
          .filter(counter => counter.id <= this.state.maxCounters)
          .map(counter => (
            <About counter={counter} />
          ))}
      </div>
    );
  }
}

export default Home;
