import React, { Component } from 'react';
import axios from 'axios';

class NewProject extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tittle: '',
      postBody: '',
      img1: '',
      img2: '',
      img3: '',
      gitUrl: '',
      img1file: null,
      img2file: null,
      img3file: null
    };
  }

  handleImgChange = event => {
    const { files, name } = event.target;
    this.setState({
      [name]: files[0],
      loaded: 0
    });

    const formData = new FormData();
    formData.append([name], files[0]);

    const options = {
      method: 'POST',
      body: formData
    };

    fetch('/api/project/upload', options)
      .then(res => {
        if (res.status === 200) {
          res.json().then(data => {
            console.log(data);
          });
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        console.error(err);
      });
  };

  handleTextChange = event => {
    const { value, name } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleClick = () => {
    const { tittle, postBody, img1, img2, img3, gitUrl } = this.state;

    const options = {
      method: 'POST',
      body: JSON.stringify({ tittle, postBody, img1, img2, img3, gitUrl }),
      headers: {
        'Content-Type': 'application/json'
      }
    };

    fetch('/api/project/add', options)
      .then(res => {
        console.log('RESPONSE FROM SERVER:');
        if (res.status === 200) {
          console.log(res);
          //ned to clear the form and alert user
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        console.error(err);
        alert('Error adding project please try again');
      });
  };

  /*      onSubmit = event => {
        event.preventDefault();
        fetch('/api/users/authenticate', {
          method: 'POST',
          body: JSON.stringify(this.state),
          headers: {
            'Content-Type': 'application/json'
          }
        })
          .then(res => {
            if (res.status === 200) {
              let pathname = this.props.location.pathname;
              pathname = pathname.replace('/login', '');
              this.props.history.push(pathname);
            } else {
              const error = new Error(res.error);
              throw error;
            }
          })
          .catch(err => {
            console.error(err);
            alert('Error logging in please try again');
          });
      }; */
  render() {
    return (
      <section className="page-section portfolio" id="newProject">
        <div className="container">
          <h2 className="page-section-heading text-center text-uppercase text-secondary mb-0">
            Dodaj projekt
          </h2>

          <div className="divider-custom">
            <div className="divider-custom-line"></div>
            <div className="divider-custom-icon">
              <i className="fas fa-star"></i>
            </div>
            <div className="divider-custom-line"></div>
          </div>
          <div className="row justify-content-center">
            <div className="col-md-8 col-md-offset-2 text-left">
              <form action="" method="POST">
                <div className="form-group">
                  <label for="tittle">
                    Title <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="tittle"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <label for="postBody">Description</label>
                  <textarea
                    rows="5"
                    className="form-control"
                    name="postBody"
                    onChange={this.handleTextChange}
                  ></textarea>
                </div>

                <div className="form-group">
                  <label for="img1file">
                    Img 1 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img1file"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img1">
                    Image1: <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="img1"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img2file">
                    Img 2 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img2file"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img2">
                    Image2: <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="img2"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img3file">
                    Img 3 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img3file"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img3">
                    Image3: <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="img3"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <label for="gitUrl">
                    GitHub Url <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="gitUrl"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <p>
                    <span className="require">*</span> - required fields
                  </p>
                </div>

                <div className="form-group">
                  <button
                    type="button"
                    class="btn btn-success btn-block"
                    onClick={this.handleClick}
                  >
                    Create
                  </button>
                  <button className="btn btn-default">Cancel</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default NewProject;
