import React, { Component } from 'react';

import FirstImpression from './first-impression.component';
import Project from './project.component';
import Portfolio from './portfolio.component';
import { thisExpression } from '@babel/types';

class Home extends Component {
  state = {
    projectsDisplayed: 2,
    projects: []
  };

  componentDidMount() {
    fetch('/api/project')
      .then(res => {
        if (res.status === 200) {
          res.json().then(data => {
            this.setState({
              projects: data
            });
          });
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        console.error(err);
      });
  }

  handleShowMoreProjects = () => {
    //console.log("handling reset");
    const projectsDisplayed = this.state.projectsDisplayed;
    projectsDisplayed++;
    this.setState({ projectsDisplayed });
  };

  render() {
    return (
      <div
        id="spy-wrapper"
        data-spy="scroll"
        data-target="#mainNav"
        data-offset="0"
      >
        <FirstImpression />
        <Portfolio />

        {this.state.projects
          .filter(
            project =>
              this.state.projects.indexOf(project) >=
              this.state.projects.length - this.state.projectsDisplayed
          )
          .reverse()
          .map(project => (
            <Project
              project={project}
              projectNr={this.state.projects.indexOf(project) + 1}
              nrOfProjects={this.state.projects.length}
              projectsDisplayed={this.state.projectsDisplayed}
              onShowMoreProjects={this.handleShowMoreProjects}
            />
          ))}
      </div>
    );
  }
}

export default Home;
