import React, { Component } from 'react';

class NewProject extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tittle: '',
      postBody: '',
      img1: null,
      img2: null,
      img3: null,
      gitUrl: ''
    };
  }

  handleImgChange = event => {
    const { files, name } = event.target;
    this.setState({
      [name]: files[0],
      loaded: 0
    });
  };

  handleTextChange = event => {
    const { value, name } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleClick = () => {
    const formData = new FormData();
    formData.append('tittle', this.state.tittle);
    formData.append('postBody', this.state.postBody);
    formData.append('img1', this.state.img1);
    formData.append('img2', this.state.img2);
    formData.append('img3', this.state.img3);
    formData.append('gitUrl', this.state.gitUrl);

    const options = {
      method: 'POST',
      data: formData
    };

    /* const options = {
      method: 'POST',
      body: JSON.stringify({
        tittle: this.state.tittle,
        gitUrl: this.state.gitUrl
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    }; */

    console.log(options);

    // Remove 'Content-Type' header to allow browser to add
    // along with the correct 'boundary'
    //delete options.headers['Content-Type'];

    fetch('/api/project/add', options).then((req, res) => {
      console.log('RESPONSE FROM SERVER:');

      console.log(res);
    });
  };

  /*      onSubmit = event => {
        event.preventDefault();
        fetch('/api/users/authenticate', {
          method: 'POST',
          body: JSON.stringify(this.state),
          headers: {
            'Content-Type': 'application/json'
          }
        })
          .then(res => {
            if (res.status === 200) {
              let pathname = this.props.location.pathname;
              pathname = pathname.replace('/login', '');
              this.props.history.push(pathname);
            } else {
              const error = new Error(res.error);
              throw error;
            }
          })
          .catch(err => {
            console.error(err);
            alert('Error logging in please try again');
          });
      }; */
  render() {
    return (
      <section className="page-section portfolio" id="newProject">
        <div className="container">
          <h2 className="page-section-heading text-center text-uppercase text-secondary mb-0">
            Dodaj projekt
          </h2>

          <div className="divider-custom">
            <div className="divider-custom-line"></div>
            <div className="divider-custom-icon">
              <i className="fas fa-star"></i>
            </div>
            <div className="divider-custom-line"></div>
          </div>
          <div className="row justify-content-center">
            <div className="col-md-8 col-md-offset-2 text-left">
              <form action="" method="POST">
                <div className="form-group">
                  <label for="tittle">
                    Title <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="tittle"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <label for="postBody">Description</label>
                  <textarea
                    rows="5"
                    className="form-control"
                    name="postBody"
                    onChange={this.handleTextChange}
                  ></textarea>
                </div>

                <div className="form-group">
                  <label for="img1">
                    Img 1 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img1"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img2">
                    Img 2 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img2"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="img3">
                    Img 3 <span className="require">*</span>
                  </label>
                  <input
                    type="file"
                    name="img3"
                    onChange={this.handleImgChange}
                  />
                </div>

                <div className="form-group">
                  <label for="gitUrl">
                    GitHub Url <span className="require">*</span>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    name="gitUrl"
                    onChange={this.handleTextChange}
                  />
                </div>

                <div className="form-group">
                  <p>
                    <span className="require">*</span> - required fields
                  </p>
                </div>

                <div className="form-group">
                  <button
                    type="button"
                    class="btn btn-success btn-block"
                    onClick={this.handleClick}
                  >
                    Create
                  </button>
                  <button className="btn btn-default">Cancel</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default NewProject;
