import React, { Component } from 'react';

import FirstImpression from './first-impression.component';
import About from './about.component';
import Portfolio from './portfolio.component';
import { thisExpression } from '@babel/types';

class Home extends Component {
  state = {
    maxCounters: 1,
    projects: []
  };

  componentDidMount() {
    fetch('/api/project')
      .then(res => {
        //console.log(res);
        if (res.status === 200) {
          let projects = [];
          let idx = 1;
          res.json().then(projectsA => {
            projectsA.map(project => {
              projects.push({
                id: idx,
                tittle: project.tittle,
                postBody: project.postBody
              });
              idx++;
            });
          });
          this.setState({ projects });
        } else {
          const error = new Error(res.error);
          throw error;
        }
      })
      .catch(err => {
        console.error(err);
      });
  }

  render() {
    return (
      <div
        id="spy-wrapper"
        data-spy="scroll"
        data-target="#mainNav"
        data-offset="0"
      >
        <FirstImpression />
        <Portfolio />

        {this.state.projects
          .filter(
            project =>
              this.state.projects.indexOf(project) < this.state.maxCounters
          )
          .map(project => (
            <About project={project} />
          ))}
      </div>
    );
  }
}

export default Home;
