import React, { Component } from 'react';

class About extends Component {
  state = {};
  render() {
    let sectionClassname;
    let h2className;
    if (this.props.counter.id % 2 === 0) {
      sectionClassname = 'page-section portfolio';
      h2className =
        'page-section-heading text-center text-uppercase text-secondary mb-0';
    } else {
      sectionClassname = 'page-section bg-primary text-white mb-0';
      h2className =
        'page-section-heading text-center text-uppercase text-white';
    }
    return (
      <section className={sectionClassname} id="about">
        <div className="container">
          <h2 className={h2className}>{this.props.counter.title}</h2>

          <div className="divider-custom divider-light">
            <div className="divider-custom-line"></div>
            <div className="divider-custom-icon">
              <i className="fas fa-star"></i>
            </div>
            <div className="divider-custom-line"></div>
          </div>

          <div className="row">
            <div className="col-lg-4 ml-auto">
              <p className="lead">{this.props.counter.content}</p>
            </div>
            <div className="col-lg-4 mr-auto">
              <p className="lead">
                You can create your own custom avatar for the masthead, change
                the icon in the dividers, and add your email address to the
                contact form to make it fully functional!
              </p>
            </div>
          </div>

          <div className="text-center mt-4">
            <a
              className="btn btn-xl btn-outline-light"
              href="https://startbootstrap.com/themes/freelancer/"
            >
              <i className="fas fa-download mr-2"></i>
              Free Download!
            </a>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
